
CREATE TABLE "customers" (
  "id" INT GENERATED BY DEFAULT AS IDENTITY,
  "lastname" text ,
  "firstname" text ,
  PRIMARY KEY ("id")
) ;


CREATE TABLE "goods" (
  "id" varchar(255)  NOT NULL,
  "flavor" text ,
  "food" text ,
  "price" DECIMAL(10,2) DEFAULT NULL,
  PRIMARY KEY ("id")
) ;



CREATE TABLE "items" (
  "receipt" int NOT NULL,
  "ordinal" int NOT NULL,
  "item" varchar(255)  DEFAULT NULL,
  PRIMARY KEY ("receipt","ordinal"),
  CONSTRAINT "items_fk_0_0" FOREIGN KEY ("item") REFERENCES "goods" ("id")
) ;


CREATE TABLE "receipts" (
  "receiptnumber" INT GENERATED BY DEFAULT AS IDENTITY,
  "date" text ,
  "customerid" int DEFAULT NULL,
  PRIMARY KEY ("receiptnumber"),
  CONSTRAINT "receipts_fk_0_0" FOREIGN KEY ("customerid") REFERENCES "customers" ("id")
) ;


