
CREATE TABLE "boats" (
  "bid" INT GENERATED BY DEFAULT AS IDENTITY,
  "name" text ,
  "color" text ,
  PRIMARY KEY ("bid")
) ;



CREATE TABLE "sailors" (
  "sid" INT GENERATED BY DEFAULT AS IDENTITY,
  "name" text ,
  "rating" int DEFAULT NULL,
  "age" int DEFAULT NULL,
  PRIMARY KEY ("sid")
);


CREATE TABLE "reserves" (
  "sid" int DEFAULT NULL,
  "bid" int DEFAULT NULL,
  "day" text ,
  CONSTRAINT "reserves_fk_0_0" FOREIGN KEY ("bid") REFERENCES "boats" ("bid"),
  CONSTRAINT "reserves_fk_1_0" FOREIGN KEY ("sid") REFERENCES "sailors" ("sid")
) ;



