

CREATE TABLE "author" (
  "author_id" INT GENERATED BY DEFAULT AS IDENTITY,
  "name" text ,
  "age" int DEFAULT NULL,
  "gender" text ,
  PRIMARY KEY ("author_id")
);



CREATE TABLE "press" (
  "press_id" INT GENERATED BY DEFAULT AS IDENTITY,
  "name" text ,
  "month_profits_billion" DOUBLE PRECISION DEFAULT NULL,
  "year_profits_billion" DOUBLE PRECISION DEFAULT NULL,
  PRIMARY KEY ("press_id")
) ;



CREATE TABLE "book" (
  "book_id" INT GENERATED BY DEFAULT AS IDENTITY,
  "title" text ,
  "book_series" text ,
  "author_id" int DEFAULT NULL,
  "press_id" int DEFAULT NULL,
  "sale_amount" text ,
  "release_date" text ,
  PRIMARY KEY ("book_id"),
  CONSTRAINT "book_fk_0_0" FOREIGN KEY ("press_id") REFERENCES "press" ("press_id"),
  CONSTRAINT "book_fk_1_0" FOREIGN KEY ("author_id") REFERENCES "author" ("author_id")
) ;



