
CREATE TABLE "book" (
  "book_id" INT GENERATED BY DEFAULT AS IDENTITY,
  "title" text ,
  "type" text ,
  "pages" int DEFAULT NULL,
  "chapters" int DEFAULT NULL,
  "audio" text ,
  "release" text ,
  PRIMARY KEY ("book_id")
) ;


CREATE TABLE "review" (
  "review_id" INT GENERATED BY DEFAULT AS IDENTITY,
  "book_id" int DEFAULT NULL,
  "rating" DOUBLE PRECISION DEFAULT NULL,
  "readers_in_million" DOUBLE PRECISION DEFAULT NULL,
  "rank" int DEFAULT NULL,
  PRIMARY KEY ("review_id"),
  CONSTRAINT "review_fk_0_0" FOREIGN KEY ("book_id") REFERENCES "book" ("book_id")
) ;


