

CREATE TABLE "country" (
  "country_id" INT GENERATED BY DEFAULT AS IDENTITY,
  "country" text ,
  "capital" text ,
  "official_native_language" text ,
  "regoin" text ,
  PRIMARY KEY ("country_id")
) ;


CREATE TABLE "driver" (
  "driver_id" INT GENERATED BY DEFAULT AS IDENTITY,
  "driver" text ,
  "country" int DEFAULT NULL,
  "age" int DEFAULT NULL,
  "car_#" DOUBLE PRECISION DEFAULT NULL,
  "make" text ,
  "points" int ,
  "laps" DOUBLE PRECISION DEFAULT NULL,
  "winnings" text ,
  PRIMARY KEY ("driver_id"),
  CONSTRAINT "driver_fk_0_0" FOREIGN KEY ("country") REFERENCES "country" ("country_id")
) ;


CREATE TABLE "team" (
  "team_id" INT GENERATED BY DEFAULT AS IDENTITY,
  "team" text ,
  "make" text ,
  "manager" text ,
  "sponsor" text ,
  "car_owner" text ,
  PRIMARY KEY ("team_id")
) ;


CREATE TABLE "team_driver" (
  "team_id" int NOT NULL,
  "driver_id" int NOT NULL,
  PRIMARY KEY ("team_id","driver_id"),
  CONSTRAINT "team_driver_fk_0_0" FOREIGN KEY ("driver_id") REFERENCES "driver" ("driver_id"),
  CONSTRAINT "team_driver_fk_1_0" FOREIGN KEY ("team_id") REFERENCES "team" ("team_id")
) ;


