
CREATE TABLE "driver" (
  "driver_id" INT GENERATED BY DEFAULT AS IDENTITY,
  "driver_name" text ,
  "entrant" text ,
  "constructor" text ,
  "chassis" text ,
  "engine" text ,
  "age" int DEFAULT NULL,
  PRIMARY KEY ("driver_id")
) ;


CREATE TABLE "race" (
  "road" INT GENERATED BY DEFAULT AS IDENTITY,
  "driver_id" int DEFAULT NULL,
  "race_name" text ,
  "pole_position" text ,
  "fastest_lap" text ,
  "winning_driver" text ,
  "winning_team" text ,
  "report" text ,
  PRIMARY KEY ("road"),
  CONSTRAINT "race_fk_0_0" FOREIGN KEY ("driver_id") REFERENCES "driver" ("driver_id")
) ;


