
CREATE TABLE "club" (
  "club_id" INT GENERATED BY DEFAULT AS IDENTITY,
  "overall_ranking" int DEFAULT NULL,
  "team_leader" text ,
  "club_name" text ,
  PRIMARY KEY ("club_id")
) ;




CREATE TABLE "member" (
  "member_id" INT GENERATED BY DEFAULT AS IDENTITY,
  "name" text ,
  "nationality" text ,
  "age" int DEFAULT NULL,
  PRIMARY KEY ("member_id")
) ;


CREATE TABLE "club_leader" (
  "club_id" int NOT NULL,
  "member_id" int NOT NULL,
  "year_join" text ,
  PRIMARY KEY ("club_id","member_id"),
  CONSTRAINT "club_leader_fk_0_0" FOREIGN KEY ("member_id") REFERENCES "member" ("member_id"),
  CONSTRAINT "club_leader_fk_1_0" FOREIGN KEY ("club_id") REFERENCES "club" ("club_id")
) ;


