
CREATE TABLE "countries" (
  "id" INT GENERATED BY DEFAULT AS IDENTITY,
  "name" text ,
  "overall_score" DOUBLE PRECISION DEFAULT NULL,
  "justice_score" DOUBLE PRECISION DEFAULT NULL,
  "health_score" DOUBLE PRECISION DEFAULT NULL,
  "education_score" DOUBLE PRECISION DEFAULT NULL,
  "economics_score" DOUBLE PRECISION DEFAULT NULL,
  "politics_score" DOUBLE PRECISION DEFAULT NULL,
  PRIMARY KEY ("id")
) ;


CREATE TABLE "languages" (
  "id" INT GENERATED BY DEFAULT AS IDENTITY,
  "name" text ,
  PRIMARY KEY ("id")
) ;


CREATE TABLE "official_languages" (
  "language_id" int NOT NULL,
  "country_id" int NOT NULL,
  PRIMARY KEY ("language_id","country_id"),
  CONSTRAINT "official_languages_fk_0_0" FOREIGN KEY ("country_id") REFERENCES "countries" ("id"),
  CONSTRAINT "official_languages_fk_1_0" FOREIGN KEY ("language_id") REFERENCES "languages" ("id")
) ;


