
CREATE TABLE "addresses" (
  "address_id" INT GENERATED BY DEFAULT AS IDENTITY,
  "address_details" varchar(255)  DEFAULT NULL,
  PRIMARY KEY ("address_id")
) ;



CREATE TABLE "products" (
  "product_id" INT GENERATED BY DEFAULT AS IDENTITY,
  "product_type_code" varchar(15)  DEFAULT NULL,
  "product_name" varchar(80)  DEFAULT NULL,
  "product_price" DOUBLE PRECISION DEFAULT NULL,
  PRIMARY KEY ("product_id")
) ;


CREATE TABLE "customers" (
  "customer_id" INT GENERATED BY DEFAULT AS IDENTITY,
  "address_id" int NOT NULL,
  "payment_method_code" varchar(15)  DEFAULT NULL,
  "customer_number" varchar(20)  DEFAULT NULL,
  "customer_name" varchar(80)  DEFAULT NULL,
  "customer_address" varchar(255)  DEFAULT NULL,
  "customer_phone" varchar(80)  DEFAULT NULL,
  "customer_email" varchar(80)  DEFAULT NULL,
  PRIMARY KEY ("customer_id")
) ;


CREATE TABLE "customer_orders" (
  "order_id" INT GENERATED BY DEFAULT AS IDENTITY,
  "customer_id" int NOT NULL,
  "order_date" TIMESTAMP NOT NULL,
  "order_status_code" varchar(15)  DEFAULT NULL,
  PRIMARY KEY ("order_id"),
  CONSTRAINT "customer_orders_fk_0_0" FOREIGN KEY ("customer_id") REFERENCES "customers" ("customer_id")
) ;

CREATE TABLE "order_items" (
  "order_item_id" int NOT NULL,
  "order_id" int NOT NULL,
  "product_id" int NOT NULL,
  "order_quantity" varchar(80)  DEFAULT NULL,
  CONSTRAINT "order_items_fk_0_0" FOREIGN KEY ("product_id") REFERENCES "products" ("product_id"),
  CONSTRAINT "order_items_fk_1_0" FOREIGN KEY ("order_id") REFERENCES "customer_orders" ("order_id")
) ;



