
CREATE TABLE "district" (
  "district_id" INT GENERATED BY DEFAULT AS IDENTITY,
  "name" text ,
  "area_km" DOUBLE PRECISION DEFAULT NULL,
  "population" DOUBLE PRECISION DEFAULT NULL,
  "density_km" DOUBLE PRECISION DEFAULT NULL,
  "government_website" text ,
  PRIMARY KEY ("district_id")
) ;


CREATE TABLE "spokesman" (
  "spokesman_id" INT GENERATED BY DEFAULT AS IDENTITY,
  "name" text ,
  "age" int DEFAULT NULL,
  "speach_title" text ,
  "rank_position" DOUBLE PRECISION DEFAULT NULL,
  "points" DOUBLE PRECISION DEFAULT NULL,
  PRIMARY KEY ("spokesman_id")
) ;


CREATE TABLE "spokesman_district" (
  "spokesman_id" INT GENERATED BY DEFAULT AS IDENTITY,
  "district_id" int DEFAULT NULL,
  "start_year" text ,
  PRIMARY KEY ("spokesman_id"),
  CONSTRAINT "spokesman_district_fk_0_0" FOREIGN KEY ("district_id") REFERENCES "district" ("district_id"),
  CONSTRAINT "spokesman_district_fk_1_0" FOREIGN KEY ("spokesman_id") REFERENCES "spokesman" ("spokesman_id")
) ;


