
CREATE TABLE "headphone" (
  "headphone_id" INT GENERATED BY DEFAULT AS IDENTITY,
  "model" text ,
  "class" text ,
  "driver-matched_db" DOUBLE PRECISION DEFAULT NULL,
  "construction" text ,
  "earpads" text ,
  "price" int DEFAULT NULL,
  PRIMARY KEY ("headphone_id")
) ;




CREATE TABLE "store" (
  "store_id" INT GENERATED BY DEFAULT AS IDENTITY,
  "name" text ,
  "neighborhood" text ,
  "parking" text ,
  "date_opened" text ,
  PRIMARY KEY ("store_id")
) ;


CREATE TABLE "stock" (
  "store_id" int NOT NULL,
  "headphone_id" int NOT NULL,
  "quantity" int DEFAULT NULL,
  PRIMARY KEY ("store_id","headphone_id"),
  CONSTRAINT "stock_fk_0_0" FOREIGN KEY ("headphone_id") REFERENCES "headphone" ("headphone_id"),
  CONSTRAINT "stock_fk_1_0" FOREIGN KEY ("store_id") REFERENCES "store" ("store_id")
) ;
