CREATE TABLE "institution" (
  "institution_id" INT GENERATED BY DEFAULT AS IDENTITY,
  "name" text ,
  "team" text ,
  "city" text ,
  "province" text ,
  "founded" DOUBLE PRECISION DEFAULT NULL,
  "affiliation" text ,
  "enrollment" DOUBLE PRECISION DEFAULT NULL,
  "endowment" text ,
  "stadium" text ,
  "capacity" DOUBLE PRECISION DEFAULT NULL,
  PRIMARY KEY ("institution_id")
) ;

CREATE TABLE "championship" (
  "institution_id" INT GENERATED BY DEFAULT AS IDENTITY,
  "nickname" text ,
  "joined" DOUBLE PRECISION DEFAULT NULL,
  "number_of_championships" DOUBLE PRECISION DEFAULT NULL,
  PRIMARY KEY ("institution_id"),
  CONSTRAINT "championship_fk_0_0" FOREIGN KEY ("institution_id") REFERENCES "institution" ("institution_id")
) ;


