CREATE TABLE "region" (
  "region_id" INT GENERATED BY DEFAULT AS IDENTITY,
  "name" text ,
  "capital" text ,
  "area" int DEFAULT NULL,
  "population" int DEFAULT NULL,
  PRIMARY KEY ("region_id")
);




CREATE TABLE "building" (
  "building_id" INT GENERATED BY DEFAULT AS IDENTITY,
  "region_id" int DEFAULT NULL,
  "name" text ,
  "address" text ,
  "number_of_stories" int DEFAULT NULL,
  "completed_year" int DEFAULT NULL,
  PRIMARY KEY ("building_id"),
  CONSTRAINT "building_fk_0_0" FOREIGN KEY ("region_id") REFERENCES "region" ("region_id")
) ;

