
CREATE TABLE "branch" (
  "branch_id" INT GENERATED BY DEFAULT AS IDENTITY,
  "manager" text ,
  "years_opened" int DEFAULT NULL,
  "location_of_office" text ,
  PRIMARY KEY ("branch_id")
) ;


CREATE TABLE "customer" (
  "customer_id" INT GENERATED BY DEFAULT AS IDENTITY,
  "name" text ,
  "nationality" text ,
  "card_credit" DOUBLE PRECISION DEFAULT NULL,
  "level_of_membership" int DEFAULT NULL,
  PRIMARY KEY ("customer_id")
) ;


CREATE TABLE "customer_order" (
  "customer_id" int NOT NULL,
  "branch_id" int NOT NULL,
  "dish_name" text NOT NULL,
  "quantity" int DEFAULT NULL,
  PRIMARY KEY ("customer_id","branch_id","dish_name"),
  CONSTRAINT "customer_order_fk_0_0" FOREIGN KEY ("branch_id") REFERENCES "branch" ("branch_id"),
  CONSTRAINT "customer_order_fk_1_0" FOREIGN KEY ("customer_id") REFERENCES "customer" ("customer_id")
) ;


