
CREATE TABLE "participants" (
  "id" INT GENERATED BY DEFAULT AS IDENTITY,
  "name" text ,
  "popularity" DOUBLE PRECISION DEFAULT NULL,
  PRIMARY KEY ("id")
) ;

CREATE TABLE "songs" (
  "id" INT GENERATED BY DEFAULT AS IDENTITY,
  "language" text ,
  "original_artist" text ,
  "name" text ,
  "english_translation" text ,
  PRIMARY KEY ("id")
) ;

CREATE TABLE "performance_score" (
  "participant_id" int NOT NULL,
  "songs_id" int NOT NULL,
  "voice_sound_quality" DOUBLE PRECISION DEFAULT NULL,
  "rhythm_tempo" DOUBLE PRECISION DEFAULT NULL,
  "stage_presence" DOUBLE PRECISION DEFAULT NULL,
  PRIMARY KEY ("participant_id","songs_id"),
  CONSTRAINT "performance_score_fk_0_0" FOREIGN KEY ("songs_id") REFERENCES "songs" ("id"),
  CONSTRAINT "performance_score_fk_1_0" FOREIGN KEY ("participant_id") REFERENCES "participants" ("id")
) ;




