
CREATE TABLE "club" (
  "club_id" INT GENERATED BY DEFAULT AS IDENTITY,
  "name" text ,
  "manager" text ,
  "captain" text ,
  "manufacturer" text ,
  "sponsor" text ,
  PRIMARY KEY ("club_id")
) ;


CREATE TABLE "player" (
  "player_id" DOUBLE PRECISION NOT NULL,
  "name" text ,
  "country" text ,
  "earnings" DOUBLE PRECISION DEFAULT NULL,
  "events_number" int DEFAULT NULL,
  "wins_count" int DEFAULT NULL,
  "club_id" int DEFAULT NULL,
  PRIMARY KEY ("player_id"),
  CONSTRAINT "player_fk_0_0" FOREIGN KEY ("club_id") REFERENCES "club" ("club_id")
) ;


