
CREATE TABLE "driver" (
  "driver_id" INT GENERATED BY DEFAULT AS IDENTITY,
  "name" text ,
  "citizenship" text ,
  "racing_series" text ,
  PRIMARY KEY ("driver_id")
) ;


CREATE TABLE "vehicle" (
  "vehicle_id" INT GENERATED BY DEFAULT AS IDENTITY,
  "model" text ,
  "build_year" text ,
  "top_speed" int DEFAULT NULL,
  "power" int DEFAULT NULL,
  "builder" text ,
  "total_production" text ,
  PRIMARY KEY ("vehicle_id")
) ;


CREATE TABLE "vehicle_driver" (
  "driver_id" int NOT NULL,
  "vehicle_id" int NOT NULL,
  PRIMARY KEY ("driver_id","vehicle_id"),
  CONSTRAINT "vehicle_driver_fk_0_0" FOREIGN KEY ("vehicle_id") REFERENCES "vehicle" ("vehicle_id"),
  CONSTRAINT "vehicle_driver_fk_1_0" FOREIGN KEY ("driver_id") REFERENCES "driver" ("driver_id")
) ;


