CREATE TABLE "platform" (
  "platform_id" INT GENERATED BY DEFAULT AS IDENTITY,
  "platform_name" text ,
  "market_district" text ,
  "download_rank" int DEFAULT NULL,
  PRIMARY KEY ("platform_id")
) ;


CREATE TABLE "player" (
  "player_id" INT GENERATED BY DEFAULT AS IDENTITY,
  "rank_of_the_year" int DEFAULT NULL,
  "player_name" text ,
  "position" text ,
  "college" text ,
  PRIMARY KEY ("player_id")
) ;

CREATE TABLE "game" (
  "game_id" INT GENERATED BY DEFAULT AS IDENTITY,
  "title" text ,
  "release_date" text ,
  "franchise" text ,
  "developers" text ,
  "platform_id" int DEFAULT NULL,
  "units_sold_millions" int DEFAULT NULL,
  PRIMARY KEY ("game_id"),
  CONSTRAINT "game_fk_0_0" FOREIGN KEY ("platform_id") REFERENCES "platform" ("platform_id")
) ;


CREATE TABLE "game_player" (
  "player_id" int NOT NULL,
  "game_id" int NOT NULL,
  "if_active" SMALLINT DEFAULT NULL,
  PRIMARY KEY ("player_id","game_id"),
  CONSTRAINT "game_player_fk_0_0" FOREIGN KEY ("game_id") REFERENCES "game" ("game_id"),
  CONSTRAINT "game_player_fk_1_0" FOREIGN KEY ("player_id") REFERENCES "player" ("player_id")
) ;




